import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import './styles.css';

import hamburguer from '../../img/hamburguer.png'
import home from '../../img/house_home_13944.png'

export default class Header extends Component{
	constructor(props) {
		super(props);
		this.state = {
			isOpen: false
		};
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick() {
		this.setState({isOpen: !this.state.isOpen});
	};

	render() {
		const { isOpen } = this.state;
		const menuMobile = isOpen ? 'ativo' : '';

		return (
			<header className="header">
				<a onClick={this.handleClick}><img src={hamburguer} alt="Ícone Menu Hamburguer" title="Ícone Menu Hamburguer"></img></a>
				<nav className={menuMobile}>
					<button onClick={this.handleClick}>X</button>
					<a href="home.html"><img src={home} alt="Ícone Home" title="Ícone Home"></img></a>
					<ul>
						<li><Link to="/sobre">Sobre mim</Link></li>
						<li><Link to="/">Portfolio</Link></li>
						<li><Link to="/">Contato</Link></li>
						<li><Link to="/">Blog</Link></li>
					</ul>
				</nav>
			</header>
		);
	}
};
